import UIKit

protocol YTPlayerBusinessLogic {
    func loadYoutubeVideo(request: YTPlayer.LoadYoutubeVideo.Request)
}

protocol YTPlayerDataStore {
    //var name: String { get set }
}

class YTPlayerInteractor: YTPlayerBusinessLogic, YTPlayerDataStore {
    var presenter: YTPlayerPresentationLogic?
    var worker: YTPlayerWorker?
    //var name: String = ""
    
    func loadYoutubeVideo(request: YTPlayer.LoadYoutubeVideo.Request) {
        let response = YTPlayer.LoadYoutubeVideo.Response(id: request.id)
        presenter?.presentLoadYoutubeVideo(response: response)
    }
}
